import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Header from '../../component/Header';
import { Card, Typography, CardContent, CardHeader, Container, Grid, Button, Box } from '@material-ui/core';
// import Footer from '../Footer';
import { useNavigate, useParams } from 'react-router-dom';



const useStyles = makeStyles(theme => ({
    gridItem: {
    },
    mainGrid: {
        marginTop: '15px'
    },
    mainImage: {
        width: '90%',
        height: '170px'
    },
    button: {
        background: 'blue',
        color: 'white'
    },
    detailButton: {
        display: 'flex',
        justifyContent: 'end'
    },
    mainHead: {
        display: 'flex',
        justifyContent: 'space-between'
    }
}));
const datas = [
    {
        id: 1,
        name: "Laptop Acer ES1-131-C7C2",
        Price: "1000$",
        image: require("../../images/image1.png"),
        description: "– CPU: 8th Gen Intel Celeron N3060 (1.6GHz up to 2.48GHz-2MB Cache)– Operating System: DOS – Memory: 2GB DDR3L(1600MHz)(free 1 slot) – Hard Drive: eMMC 32GB – Optical Drive: None"
    },

];
export default function About() {
    const classes = useStyles();
    return (
        <div><Header />
            {datas.map(item => (
                <Container>
                    <Grid container spacing={4} className={classes.mainGrid} >
                        <Grid item xs={12} md={6} >
                            <Card>
                                <CardContent>
                                    <CardContent>
                                        <img src={item.image} className={classes.mainImage} />
                                    </CardContent>
                                </CardContent>
                            </Card>
                        </Grid>
                        <Grid item xs={12} md={6} >
                            <CardContent>
                                <Box className={classes.mainHead}>
                                    <Typography variant='h5'>{item.name}</Typography>
                                </Box>
                                <CardContent>
                                    <Typography>{item.description}</Typography>
                                </CardContent>
                            </CardContent>
                        </Grid>
                    </Grid>
                </Container>
            ))}
            {/* <Footer /> */}
        </div>
    );
}
