import React, { useState } from "react";
import Header from "../../component/Header";
import { Typography } from "@material-ui/core";
import TextField from "@material-ui/core/TextField";
import makeStyles from "@material-ui/core/styles/makeStyles";
import ListItem from "@material-ui/core/ListItem";
import ListItemIcon from "@material-ui/core/ListItemIcon";
import ListItemText from "@material-ui/core/ListItemText";


import List from "@material-ui/core/List";
import ListSubheader from "@material-ui/core/ListSubheader";
import ListItemSecondaryAction from "@material-ui/core/ListItemSecondaryAction";
import ListItemAvatar from "@material-ui/core/ListItemAvatar";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Avatar from "@material-ui/core/Avatar";
import Collapse from "@material-ui/core/Collapse";
import Checkbox from "@material-ui/core/Checkbox";
import { Container, Button, Box } from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  gridItem: {},
  mainGrid: {
    marginTop: "15px",
  },
  mainImage: {
    width: "100%",
    height: "140px",
  },
  button: {
    background: "blue",
    color: "white",
  },
  detailButton: {
    display: "flex",
    justifyContent: "end",
  },
  mainHead: {
    display: "flex",
    justifyContent: "space-between",
  },
  jumbutron: {
    background: "grey",
    padding: "30px",
  },
  mainImage: {
    width: "100%",
  },
  jumbotron: {
    textAlign: "center",
    color: "white",
  },
}));
export default function ContactUs({ open }) {
  const classes = useStyles();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = () => { };

  const image1 = require("../../images/image1.png");
  const image2 = require("../../images/image2.PNG");
  const image3 = require("../../images/image3.PNG");
  const image4 = require("../../images/image4.PNG");

  const banner = require("../../images/banner.png");

  //

  return (
    <div>
      <Header />
      {/* <Container> */}
        {/* <Box className={classes.jumbutron}> */}
          <div className={classes.jumbotron}>
            <img src={banner} className={classes.mainImage} alt="Snow" />
            <h1 class="display-4">Welcome To Value Customer</h1>
            <p class="lead">
              This is a simple hero unit, a simple jumbotron-style component for
              calling extra attention to featured content or information.
            </p>
            <p>
              It uses utility classes for typography and spacing to space
              content out within the larger container.
            </p>
          </div>
        {/* </Box> */}

        <Grid container spacing={4} className={classes.mainGrid}>
          <Grid item xs={12} md={6} className={classes.gridItem}>
            <div className={classes.root}>
              <Typography variant="h3" gutterBottom>
                Contact Us
              </Typography>
              <form noValidate autoComplete="off" className={classes.container}>
                <div className={classes.row}>
                  <TextField
                    label="Full Name"
                    margin="normal"
                    variant="outlined"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    fullWidth
                  />
                  <TextField
                    label="Email Address"
                    margin="normal"
                    variant="outlined"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    fullWidth
                  />
                </div>
                <br />
                <TextField
                  multiline
                  rows="4"
                  label="Message"
                  margin="normal"
                  variant="outlined"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  fullWidth
                />
                <br />
                <Button
                  fullWidth
                  variant="contained"
                  color="secondary"
                  onClick={handleSubmit}
                >
                  Submit
                </Button>
              </form>
            </div>
          </Grid>
          <Grid item xs={12} md={6} className={classes.gridItem}>
            <Box>
              <Typography variant="h3" gutterBottom>
                Detail Information
              </Typography>
              <ListItem>
                {/* <ListItemIcon><LocalPostOffice/></ListItemIcon> */}
                <ListItemText primary="  Building #66, Street 63 Corner St 154, Sangkat Phsar Thmei I, Khan Doun Penh, Phnom Penh, Cambodia."></ListItemText>
              </ListItem>
              <ListItem>
                {/* <ListItemIcon><MailIcon/></ListItemIcon> */}
                <ListItemText primary="sales@ptc-computer.com.kh"></ListItemText>
              </ListItem>
              <ListItem>
                {/* <Chip icon={<MdPhone />} label="Call me" /> */}
                <ListItemText primary="0960606060"></ListItemText>
              </ListItem>
              <ListItem>
                {/* <Chip icon={<MdPhone />} label="Call me" /> */}
                <ListItemText primary="010498732"></ListItemText>
              </ListItem>
            </Box>
          </Grid>
        </Grid>
      {/* </Container> */}
    </div>
  );
}
