import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Header from '../../component/Header';
import { Card, Typography, CardContent, CardHeader, Container, Grid, Button, Box } from '@material-ui/core';
import Footer from '../Footer';
import { useNavigate,useParams } from 'react-router-dom';



const useStyles = makeStyles(theme => ({
    gridItem: {
    },
    mainGrid: {
        marginTop: '15px'
    },
    mainImage: {
        width: '100%',
        height: '140px'
    },
    button: {
        background: 'blue',
        color: 'white'
    },
    detailButton: {
        display: 'flex',
        justifyContent: 'end'
    },
    mainHead: {
        display: 'flex',
        justifyContent: 'space-between'
    }
}));
const datas = [
    {
        id: 1,
        name: "Rolex1",
        Price: "1000$",
        image: require("../../images/image1.png"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
    },
    {
        id: 2,
        name: "Rolex2",
        Price: "1000$",
        image: require("../../images/image2.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
   
   
   
];
export default function About() {
    const classes = useStyles();
    const image1 = require("../../images/image1.png")
    const image2 = require("../../images/image2.PNG")
    const image3 = require("../../images/image3.PNG")
    const image4 = require("../../images/image4.PNG")
    const image5 = require("../../images/image5.PNG")
    const image6 = require("../../images/image6.PNG")
    const image7 = require("../../images/image7.PNG")
    const image8 = require("../../images/image8.PNG")

    const navigate = useNavigate();
    const {itemDetail} = useParams();
    console.log("itemDetaillll:",itemDetail);
    return (
        <div><Header />
        {datas.map(item => (
            <Container>
                <Grid container spacing={4} className={classes.mainGrid} >
                    <Grid item xs={12} md={6} >
                        <Card>
                            <CardContent>
                                <Box className={classes.mainHead}>
                                    <Typography variant='h5'>{item.name}</Typography>
                                </Box>
                                <CardContent>

                                    <img src={item.image} className={classes.mainImage} />
                                </CardContent>
                            </CardContent>
                        </Card>
                    </Grid>
                    <Grid item xs={12} md={6} >
                            <CardContent>
                                <Box className={classes.mainHead}>
                                    <Typography variant='h5'>{item.name}</Typography>
                                </Box>
                                <CardContent>
                                    <Typography>{item.description}</Typography>
                                </CardContent>
                            </CardContent>
                    </Grid>
                </Grid>
            </Container>
        ))}


        {/* <Footer /> */}

    </div>
    );
}
