import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Header from '../../component/Header';
import { Card, Typography, CardContent, CardHeader, Container, Grid, Button, Box } from '@material-ui/core';
import Footer from '../Footer';
import { useNavigate, useParams } from 'react-router-dom';



const useStyles = makeStyles(theme => ({
    gridItem: {
    },
    mainGrid: {
        marginTop: '15px'
    },
    mainImage: {
        width: '90%',
        height: '170px'
    },
    button: {
        background: 'blue',
        color: 'white'
    },
    detailButton: {
        display: 'flex',
        justifyContent: 'end'
    },
    mainHead: {
        display: 'flex',
        justifyContent: 'space-between'
    }
}));

const datas = [
    {
        id: 1,
        name: "Rolex1",
        Price: "1000$",
        image: require("../../images/image1.png"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
    },
    {
        id: 2,
        name: "Rolex2",
        Price: "1000$",
        image: require("../../images/image2.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
    {
        id: 3,
        name: "Rolex3",
        Price: "1000$",
        image: require("../../images/image3.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
    {
        id: 4,
        name: "Rolex4",
        Price: "1000$",
        image: require("../../images/image4.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
    {
        id: 5,
        name: "Rolex5",
        Price: "1000$",
        image: require("../../images/image5.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
    {
        id: 6,
        name: "Rolex6",
        Price: "1000$",
        image: require("../../images/image6.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
    {
        id: 7,
        name: "Rolex7",
        Price: "1000$",
        image: require("../../images/image7.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    },
    {
        id: 8,
        name: "Rolex8",
        Price: "1000$",
        image: require("../../images/image8.PNG"),
        description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

    }
];
export default function Detail() {
    const classes = useStyles();
    const {itemDetail} = useParams();
    console.log('itemddds',itemDetail);
    const result = datas.filter(item => item.id === itemDetail)
    console.log('result',result);
    return (
        <div><Header />
             {datas.map(item => (
                <Container>
                    <Grid container spacing={4} className={classes.mainGrid} >
                        <Grid item xs={12} md={6} >
                            <Card>
                                <CardContent>
                                    <CardContent>
                                        <img src={item.image} className={classes.mainImage} />
                                    </CardContent>
                                </CardContent>
                            </Card>
                        </Grid>
                        <Grid item xs={12} md={6} >
                            <CardContent>
                                <Box className={classes.mainHead}>
                                    <Typography variant='h5'>{item.name}</Typography>
                                </Box>
                                <CardContent>
                                    <Typography>{item.description}</Typography>
                                </CardContent>
                            </CardContent>
                        </Grid>
                    </Grid>
                </Container>
            ))} 
        </div>
    );
}
