import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Header from '../../component/Header';
import { Grid, Box, Card, Typography, AvatarGroup } from '@material-ui/core';
import { MAvatar, MLabel } from '~/@material-extend';
import createAvatar from '~/utils/createAvatar';
import moment from 'moment';
import { isEmpty } from 'react-redux-firebase';

const useStyles = makeStyles({
})
export default function About({open}) {
const classes = useStyles();
  return (
    <div><Header/>
    <Card className={classes.card}>
      <Grid item xs={12} sm={12} md={12}>
        <Box
          sx={{
            display: 'flex',
            flexDirection: 'column'
          }}
        >
          <Box sx={{ display: 'flex', flexDirection: 'row' }}>
            <MAvatar
              src={s3_file_url}
              alt={project.name}
              sx={{
                mt: 1,
                ml: 1,
                borderWidth: 2,
                borderStyle: 'solid',
                borderColor: 'common.white',
                width: { xs: 60, md: 96 },
                height: { xs: 60, md: 96 }
              }}
            >
            </MAvatar>
            <Box sx={{ mx: 2, mt: 2 }}>
              <Typography variant="h6">{project.name}</Typography>
              <Typography variant="body2" sx={{ my: 1 }}>
                Cost: &nbsp;
                <Typography
                  variant="span"
                  sx={{ fontSize: 18, fontWeight: 'bold' }}
                >
                  {project.cost} {project.currency}
                </Typography>
              </Typography>
             
              <MLabel
                variant="filled"
                color={project.is_close ? 'error' : 'primary'}
              >
                {project.is_close ? 'Closed' : 'Open'}
              </MLabel>
            </Box>
          </Box>

          <Box
            sx={{
              mx: 2,
              my: 2,
              display: 'flex',
              flexDirection: 'row',
              justifyContent: 'space-between'
            }}
          >
            <Box>
              <Typography variant="body2" color="textSecondary">
                {`Start Date: ${startDate}`}
              </Typography>
              <Typography variant="body2" color="textSecondary">
                {`End Date: ${endDate}`}
              </Typography>
            </Box>
              <Box sx={{ mt: 3 }}>
                <AvatarGroup max={4} className={classes.avatarGroup}>
                    <MAvatar
                      key={member.id}
                      alt={member.fullname}
                      src={member.s3_file_url}
                    />
                </AvatarGroup>
              </Box>
          </Box>
        </Box>
      </Grid>
    </Card>
    </div>
  );
}
