import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import Box from '@material-ui/core/Box';
import Link from '@material-ui/core/Link';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
mainMenu: {
   backgroundColor:'rgb(0, 51, 157)',
   padding:'10px',
   marginTop:'0px'
},
buttonItem: {
    color:'white'
}
})
export default function Header() {
const classes = useStyles();
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [open, setOpen] = useState(true);
  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };


  const handleClose = () => {
    setAnchorEl(null);
  };
  const handlOpen =() => {
      setOpen(true);
  }

  return (
    <Box className={classes.mainMenu}>
      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem} onClick={handlOpen}
      >
        HOME
      </Button>
      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem} 
      >
        ABOUT US
      </Button>
      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem} 
      >
        CONTACT US
      </Button>

      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem} onClick={handleClick}
      >
        ALL PRODUCTS
      </Button>
      <Menu
        id="simple-menu"
        anchorEl={anchorEl}
        keepMounted
        open={Boolean(anchorEl)}
        onClose={handleClose}
      >
        <MenuItem onClick={handleClose}>Computer</MenuItem>
        <MenuItem onClick={handleClose}>Phone</MenuItem>y
        <MenuItem onClick={handleClose}>Watch</MenuItem>
      </Menu>
    </Box>
  );
}
