import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Header from '../../component/Header';
import { Card, Typography, CardContent, CardHeader, Container, Grid, Button, Box } from '@material-ui/core';
import Footer from '../Footer';
import { useNavigate, useParams } from 'react-router-dom';



const useStyles = makeStyles(theme => ({
    gridItem: {
    },
    mainGrid: {
        marginTop: '15px'
    },
    mainImage: {
        width: '100%',
        height: '140px'
    },
    button: {
        background: 'blue',
        color: 'white'
    },
    detailButton: {
        display: 'flex',
        justifyContent: 'end'
    },
    mainHead: {
        display: 'flex',
        justifyContent: 'space-between'
    }
}));
export default function Computer() {
    const classes = useStyles();
    const navigate = useNavigate();

    const ondetail = (item) => {
        navigate(`/detail/${item}`);

    };
    const datas = [
        {
            id: 1,
            name: "Rolex1",
            Price: "1000$",
            image: require("../../images/image1.png"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
        },
        {
            id: 2,
            name: "Rolex2",
            Price: "1000$",
            image: require("../../images/image2.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
            
        },
        {
            id: 3,
            name: "Rolex3",
            Price: "1000$",
            image: require("../../images/image3.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
            
        },
        {
            id: 4,
            name: "Rolex4",
            Price: "1000$",
            image: require("../../images/image4.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
            
        },
        {
            id: 5,
            name: "Rolex5",
            Price: "1000$",
            image: require("../../images/image5.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
            
        },
        {
            id: 6,
            name: "Rolex6",
            Price: "1000$",
            image: require("../../images/image6.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."
            
        },
        {
            id: 7,
            name: "Rolex7",
            Price: "1000$",
            image: require("../../images/image7.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

        },
        {
            id: 8,
            name: "Rolex8",
            Price: "1000$",
            image: require("../../images/image8.PNG"),
            description: "This impressive paella is a perfect party dish and a fun meal to cook together with your guests. Add 1 cup of frozen peas along with the mussels, if you like."

        }
    ];

    return (
        <div><Header />
            <>
                <Container>
                    {datas.map(item => (
                        <Grid container spacing={4} className={classes.mainGrid} >
                            <Grid item xs={12} md={3} className={classes.gridItem}>
                                <Card>
                                    <CardContent>
                                        <Box className={classes.mainHead}>
                                            <Typography variant='h5'>{item.name}</Typography>
                                            <Button variant="contained" size="small" className={classes.button} onClick={()=> onDetail(item)}>
                                                Detail
                                            </Button>
                                        </Box>
                                        <CardContent>

                                            <img src={item.image} className={classes.mainImage} />
                                            <Typography variant="body2" color="text.secondary">
                                               {item.description}
                                            </Typography>
                                        </CardContent>
                                    </CardContent>
                                </Card>
                            </Grid>
                        </Grid>
                    ))}
                </Container>
            </>
            <Footer />

        </div>
    );
}
