      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem} 
      >
        ABOUT US
      </Button>
      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem} 
      >
        CONTACT US
      </Button>

      <Button aria-controls="simple-menu" aria-haspopup="true" className={classes.buttonItem}
      >
        ALL PRODUCTS
      </Button>
      {/* <Menu
